package javapgm1;

public class UnaryOpSample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int no1=-1;
		System.out.println("Number 1= "+no1);
		
		no1=-no1;   
		System.out.println("After applying unary minus Number 1= "+no1);
		
		--no1;
		System.out.println("After applying unary decrement Number 1= "+no1);
		
		no1++;
		System.out.println("After applying unary increment Number 1= "+no1);
	}

}
