package javapgm1;

public class swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int a=324;
    int b=456;
    int t;
    System.out.println("**********Before swapping**********");
    System.out.println("a="+a+" b="+b);
    t=a;
    a=b;
    b=t;
    
    System.out.println("**********After Swapping***********");
    System.out.println("a="+a+" b="+b);
    
    
    
	}

}
